w:r.w:t:
fc = 62.64e9;        % Center frequency in Hz
fs = 2e9;            % Sampling frequency in Hz
Tc = 15.2e-6;        % Chirp duration in seconds
guard_time = 0.1e-6; % Guard time at the end of each chirp

combinations = int2bit(0:2^13-1,13)';
bw_values = linspace(400e6, 800e6, 2^7);
freq_values = linspace(-100e6, 100e6, 2^6);
selected_bw = bw_values(bit2int(combinations(:,1:7)',7,true) + 1);
selected_freq = freq_values(bit2int(combinations(:,8:13)',6,true) + 1);

t = linspace(0, Tc, fs * Tc);

pure_chirps = zeros(8192,30400);

for k = 1:8192
    f0 = selected_freq(k) ; 
    f1 = f0 + selected_bw(k) ;
    temp = chirp(t,f0,Tc,f1,'linear',0,'complex');
    pure_chirps(k, :) = temp/norm(temp);
end
